export default function validation(form, setErrors, errors, weightH, heightH) {
  if (!form.name) {
    setErrors({ ...errors, name: "Nombre vacio" });
    return errors;
  }
  const newErrors = {};

  if (!/^[ a-zA-ZñÑáéíóúÁÉÍÓÚ]+$/.test(form.name)) {
    newErrors.name = "Nombre invalido";
  }

  if (form.weight.length) {
    setErrors({ ...errors, weight: "Falta un Peso" });
  } else {
    return errors;
  }

  setErrors(newErrors);
  return newErrors;
}
