import { useEffect, useState } from "react";
import Cards from "../../Components/Cards/Cards";
import { useDispatch } from "react-redux";
import { getDogs, getFiltAZ, getFiltApi, getFiltBdd, getFiltPeso, getFiltZA, getName } from "../../Redux/actions";

const Home = ()=>{

  const dispatch = useDispatch();

  const [name, setName] = useState("")

  const[filtro, setFiltro] = useState("")

  const[orden, setOrden] = useState("")


  const handleChange =(event) =>{
    setName(event.target.value)
  }
  
  const handleFilter =(event) =>{
    setFiltro(event.target.value)
  }

  const handleOrder =(event) =>{
    setOrden(event.target.value)
  }


  const onFilter = () => {
    if(!filtro){
      if(!orden){
        dispatch(getDogs())}
      if(orden === "normal"){
        dispatch(getFiltAZ(dispatch(getDogs())))
      }
    }if(filtro === "bdd"){
      dispatch(getFiltBdd())
    }if(filtro === "api"){
      dispatch(getFiltApi())
    }
  };

  const onOrden = () => {
    if(!orden){
      dispatch(getDogs())
    }if(orden === "normal"){
      dispatch(getFiltAZ)
    }if(orden=== "inverso"){
      dispatch(getFiltZA())
    }if(filtro === "liviano"){
      dispatch(getFiltPeso())
    }
  };

  const onSearch =()=>{
    if(!name){
      dispatch(getDogs())
    }else{
    dispatch(getName(name))}
  }
    
  useEffect(()=>{
      dispatch(getDogs())
    },[dispatch])

    return(
        <>
          <div>
          <input type='search' value={name} onChange={handleChange}/>
          <p onClick={onSearch()}/>
          </div>

          <div>
        <select  onChange={handleFilter}>
          <option value="reset">Reset filter</option>
          <option value="api">Api</option>
          <option value="bdd">Base de Datos</option>
        </select>
        <p onClick={onFilter()}/>
      </div>

      <div>
        <select  onChange={handleOrder}>
          <option value="normal">A-Z</option>
          <option value="inverso">Z-A</option>
          <option value="pesado">Pesado - Liviano</option>
          <option value="livian">Liviano - Pesado</option>
        </select>
        <p onClick={onOrden()}/>
      </div>

          <Cards/>

          <button id="prev-button">Anterior</button>
          <button id="next-button">Siguiente</button>
        </>
    )
}

export default Home;
