export default function validation(form, setErrors, errors, weightH, heightH) {
  const newErrors = {};

  if (!form.name) {
    newErrors.name = "Ingrese Nombre";
    return errors;
  }

  if (!/^[ a-zA-ZñÑáéíóúÁÉÍÓÚ]+$/.test(form.name)) {
    newErrors.name = "Nombre invalido";
  }

  if ([form.weight].length < 4) {
    newErrors.weight = "Falta especificar peso";
  }

  setErrors(newErrors);
  return newErrors;
}
