import {
  FILT_BDD,
  FILT_API,
  GET_DETAIL,
  GET_DOGS,
  GET_NAME,
  GET_ALL_DOGS,
  GET_ALL_TEMPERAMENTS,
} from "./types";

const initialState = { dogs: [], filt: [], temperaments: [] };

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_ALL_DOGS:
      return { ...state, dogs: action.payload, filt: action.payload };
    case GET_ALL_TEMPERAMENTS:
      return { ...state, temperaments: action.payload };
    case GET_DOGS:
      return { ...state, filt: action.payload };
    case GET_DETAIL:
      const detail = state.filt.filter((dog) => dog.id === id);
      return { ...state, filt: detail };
    case FILT_BDD:
      return { ...state, filt: action.payload };
    case FILT_API:
      return { ...state, filt: action.payload };
    case GET_NAME:
      return { ...state, filt: action.payload };
    default:
      return { ...state };
  }
};

export default rootReducer;
