import axios from "axios";
import { GET_DOGS, GET_DETAIL, FILTER, ORDER_BY } from "./types";

export const getDogs = () => {
  return async function (dispatch) {
    const dogsData = await axios.get("http://localhost:3001/dogs");
    const dogs = dogsData.data;

    dispatch({ type: GET_DOGS, payload: dogs });
  };
};

export const getDetail = (id) => {
  return async function (dispatch) {
    const detailData = await axios.get(`http://localhost:3001/dogs/${id}`);
    const detail = detailData.data;
    dispatch({ type: GET_DETAIL, payload: detail });
  };
};

export const getFilt = (id, name, weight, temperaments) => {
  const temp = [];

  switch ((id, name, weight, temperaments)) {
    case id:
      return async function (dispatch) {
        const data = await axios.get("http://localhost:3001/dogs");
        const dogs = data.data;
        const final = await dogs.filter((dog) => dog.id === id);
        dispatch({ type: FILTER, payload: final });
      };
    case name:
      return async function (dispatch) {
        const data = await axios.get("http://localhost:3001/dogs");
        const dogs = data.data;
        const final = await dogs.filter((dog) => dog.name === name);
        dispatch({ type: FILTER, payload: final });
      };
    case weight:
      return async function (dispatch) {
        const data = await axios.get("http://localhost:3001/dogs");
        const dogs = data.data;
        const final = await dogs.filter((dog) => dog.weight === weight);
        dispatch({ type: FILTER, payload: final });
      };
    case temperaments:
      temp.push(temperaments);
      return async function (dispatch) {
        const data = await axios.get("http://localhost:3001/dogs");
        const dogs = data.data;
        const perrosFiltrados = [];

        dogs.forEach((dog) => {
          const temperamentos = dog.temperamentos
            .split(",")
            .map((temperamento) => temperamento.trim());
          temperamentos.forEach((temperamento) => {
            if (temp.includes(temperamento)) {
              perrosFiltrados.push(dog);
            }
          });
        });

        dispatch({ type: FILTER, payload: perrosFiltrados });
      };
    default:
      break;
  }
};

export const orderBy = (a, d) => {
  switch ((a, d)) {
    case a:
      return {
        type: ORDER_BY,
        payload: a,
      };
    case d:
      return {
        type: ORDER_BY,
        payload: d,
      };
    default:
      return {
        type: ORDER_BY,
        payload: a,
      };
  }
};
