import { useEffect, useState } from "react";
import Cards from "../../Components/Cards/Cards";
import { useDispatch } from "react-redux";
import { getDogs, getFilt, getName } from "../../Redux/actions";

const Home = ()=>{

  const dispatch = useDispatch();

  const [name, setName] = useState("")

  const[filtro, setFiltro] = useState("")


  const handleChange =(event) =>{
    setName(event.target.value)
  }
  
  const handleFilter =(event) =>{
    setFiltro(event.target.value)
  }

  const onFilter = () => {
    if (filtro.nombre) {
      dispatch(getFilt("nombre"));
    }
    if (filtro.peso) {
      dispatch(getFilt("peso"));
    }
  };

  const onSearch =()=>{
    if(!name){
      dispatch(getDogs())
    }else{
    dispatch(getName(name))}
  }
    
  useEffect(()=>{
      dispatch(getDogs())
    },[dispatch])

    return(
        <>
          <div>
          <input type='search' value={name} onChange={handleChange}/>
          <p onClick={onSearch()}/>
          </div>

          <div>
        <select  onChange={handleFilter}>
          <option value={filtro.reset}>Reset filter</option>
          <option value={filtro.nombre}>Nombre</option>
          <option value={filtro.peso}>Peso</option>
          <option value="api">Api</option>
          <option value="bdd">Base de Datos</option>
        </select>
        <button onClick={onFilter}>Filtrar</button>
      </div>

          <div>
          <select>
              <option value=""></option>
              <option value="ascendente">Ascendente</option>
              <option value="descendentes">Descendente</option>
          </select>
          </div>

          <Cards/>

          <button id="prev-button">Anterior</button>
          <button id="next-button">Siguiente</button>
        </>
    )
}

export default Home;
