import axios from "axios";
import {
  GET_DOGS,
  GET_DETAIL,
  FILT_BDD,
  FILT_API,
  FILT_PESO,
  GET_NAME,
  ORDER_AZ,
} from "./types";

export const getDogs = () => {
  return async function (dispatch) {
    const dogsData = await axios.get("http://localhost:3001/dogs");
    const dogs = dogsData.data;

    dispatch({ type: GET_DOGS, payload: dogs });
  };
};

export const getDetail = (id) => {
  return async function (dispatch) {
    const detailData = await axios.get(`http://localhost:3001/dogs/${id}`);
    const detail = detailData.data;
    dispatch({ type: GET_DETAIL, payload: detail });
  };
};

export const getFiltBdd = () => {
  return async function (dispatch) {
    const data = await axios.get("http://localhost:3001/dogs");
    const dogs = data.data;
    const final = await dogs
      .filter((dog) => typeof dog.id === "string")
      .sort((a, b) => a.name.localeCompare(b.name));
    dispatch({ type: FILT_BDD, payload: final });
  };
};

export const getFiltApi = () => {
  return async function (dispatch) {
    const data = await axios.get("http://localhost:3001/dogs");
    const dogs = data.data;
    const final = await dogs
      .filter((dog) => typeof dog.id === "number")
      .sort((a, b) => a.name.localeCompare(b.name));
    dispatch({ type: FILT_API, payload: final });
  };
};

export const getFiltPeso = () => {
  return async function (dispatch) {
    const data = await axios.get("http://localhost:3001/dogs");
    const dogs = data.data;
    if (typeof dogs.id === "number") {
      const final = dogs.sort((a, b) => {
        return a.weight.metric.slice(3) - b.weight.metric.slice(3);
      });
      dispatch({ type: FILT_PESO, payload: final });
    } else {
      const final = dogs.sort((a, b) => {
        return a.weight.slice(3) - b.weight.slice(3);
      });
      dispatch({ type: FILT_PESO, payload: final });
    }
  };
};

export const getFiltAZ = () => {
  return async function (dispatch) {
    const dogsData = await axios.get("http://localhost:3001/dogs");
    const dogs = dogsData.data;
    const final = dogs.sort((a, b) => a.name.localeCompare(b.name));

    dispatch({ type: ORDER_AZ, payload: final });
  };
};

export const orderBy = (a, d) => {
  switch ((a, d)) {
    case a:
      return {
        type: ORDER_BY,
        payload: a,
      };
    case d:
      return {
        type: ORDER_BY,
        payload: d,
      };
    default:
      return {
        type: ORDER_BY,
        payload: a,
      };
  }
};

export const getName = (name) => {
  return async function (dispatch) {
    const data = await axios.get("http://localhost:3001/dogs");
    const dogs = data.data;
    const final = await dogs.filter((dog) => dog.name.includes(name));
    dispatch({ type: GET_NAME, payload: final });
  };
};
