import { useEffect, useState } from "react";
import Cards from "../../Components/Cards/Cards";
import { useDispatch } from "react-redux";
import { getDogs, getFiltApi, getFiltBdd, getName,  getTemperaments, getTemp, getPag } from "../../Redux/actions";




const Home = ()=>{
  const dispatch = useDispatch();

  const [name, setName] = useState("")

  const[filtro, setFiltro] = useState("")

  const[orden, setOrden] = useState("")

  const[temperament, setTemperament] = useState("")

  const[pag, setPag] = ("''")

  let contador = 0

  const handleChange =(event) =>{
    setName(event.target.value)
  }
  
  const handleFilter =(event) =>{
    setFiltro(event.target.value)
  }

  const handleOrder =(event) =>{
    setOrden(event.target.value)
  }

  const handleTemp = (event)=>{
    setTemperament(event.target.value)
  }


  const onFilter = () => {
    if(filtro === "reset"){
      dispatch(getDogs(orden))
    }if(filtro === "bdd"){
      dispatch(getFiltBdd(orden))
    }if(filtro === "api"){
      dispatch(getFiltApi(orden))
    }if(filtro === "none"){
      dispatch(getFiltApi(orden))
    }
  };


  const onSearchName =()=>{
    dispatch(getName(name))
  }

  const onSearchTemp =()=>{
    dispatch(getTemp(temperament))
  }


  const handleKeyPress =(event)=>{
    if (event.key === "Enter" && event.target.value === name) {
      onSearchName();
    }if(event.key === "Enter" && event.target.value === temperament){
      onSearchTemp();
    }if(!event.target.value && event.key === "Enter"){
      dispatch(getDogs())
    }
  }
  
  const handlePag = (event)=>{
    if(event.target.value === "next"){
      contador ++
      console.log(pag)
     setPag(contador)
     dispatch(getPag(pag))
    }if(event.target.value === "prev"){
      contador --
      setPag(contador)
     dispatch(getPag(pag))
    }
  }

    
  useEffect(()=>{
      dispatch(getDogs());
      dispatch(getTemperaments())
    },[dispatch, pag])

    return(
        <>
            <div>
              <input type='search' value={name} onChange={handleChange} onKeyPress={handleKeyPress} placeholder="Ingrese Una Raza" />
            </div>
            
            <div>
              <input type='search' value={temperament} onChange={handleTemp} onKeyPress={handleKeyPress} placeholder="Ingrese Temperamentos" />
            </div>

            <div>
              <select  onChange={handleFilter}>
                <option value="reset">All Dogs</option>
                <option value="api">Api</option>
                <option value="bdd">Base de Datos</option>
              </select>
              <p onClick={onFilter()}/>
            </div>

            <div>
              <select  onChange={handleOrder}>
                <option value="normal">A-Z</option>
                <option value="inverso">Z-A</option>
                <option value="pesado">Pesado - Liviano</option>
                <option value="liviano">Liviano - Pesado</option>
              </select>
            </div>

            <Cards/>

            <button id="prev-button" value="next" onClick={handlePag} >Anterior</button>
            <button id="next-button" value="prev" onClick={handlePag} >Siguiente</button>

        </>
    )
}

export default Home;
