import { useEffect, useState } from "react";
import Cards from "../../Components/Cards/Cards";
import { useDispatch } from "react-redux";
import { getDogs, getFiltApi, getFiltBdd, getName,  getTemperaments } from "../../Redux/actions";
import { Link } from "react-router-dom";

let pag = 0

const Home = ()=>{
  const dispatch = useDispatch();

  const [name, setName] = useState("")

  const[filtro, setFiltro] = useState("")

  const[orden, setOrden] = useState("")

  const handleChange =(event) =>{
    setName(event.target.value)
  }
  
  const handleFilter =(event) =>{
    setFiltro(event.target.value)
  }

  const handleOrder =(event) =>{
    setOrden(event.target.value)
  }


  const onFilter = () => {
    if(filtro === "reset"){
      dispatch(getDogs(orden))
    }if(filtro === "bdd"){
      dispatch(getFiltBdd(orden))
    }if(filtro === "api"){
      dispatch(getFiltApi(orden))
    }if(filtro === "none"){
      dispatch(getFiltApi(orden))
    }
  };


  const onSearch =()=>{
    dispatch(getName(name))
  }

  const handleKeyPress =(event)=>{
    if (event.key === "Enter") {
      onSearch();
    }
  }
  
  const handlePag = (event)=>{
    if(event.value === "next"){
      return pag = (pag + 1)
    }if(event.value === "prev"){
      return pag = pag - 1
  }
}

    
  useEffect(()=>{
      dispatch(getDogs());
      dispatch(getTemperaments())
    },[dispatch])

    return(
        <>
            <div>
              <input type='search' value={name} onChange={handleChange} onKeyPress={handleKeyPress} />
            </div>

            <div>
              <select  onChange={handleFilter}>
                <option value="reset">All Dogs</option>
                <option value="api">Api</option>
                <option value="bdd">Base de Datos</option>
              </select>
              <p onClick={onFilter()}/>
            </div>

            <div>
              <select  onChange={handleOrder}>
              <option value="none">Sin Filtro</option>
                <option value="normal">A-Z</option>
                <option value="inverso">Z-A</option>
                <option value="pesado">Pesado - Liviano</option>
                <option value="liviano">Liviano - Pesado</option>
              </select>
            </div>

            <Cards/>

            <Link to={`/home/${pag}`}>
            <button id="prev-button" value="next" onClick={handlePag} >Anterior</button>
            </Link>

            <Link to={`/home/${pag}`}>
            <button id="next-button" value="prev" onClick={handlePag} >Siguiente</button>
            </Link>

        </>
    )
}

export default Home;
