import {
  FILT_BDD,
  FILT_API,
  GET_DETAIL,
  GET_DOGS,
  GET_NAME,
  GET_ALL_DOGS,
  GET_ALL_TEMPERAMENTS,
} from "./types";

const initialState = { dogs: [], filt: [], temperaments: [] };

const rootReducer = (state = initialState, { type, payload }) => {
  switch (type) {
    case GET_ALL_DOGS:
      return { ...state, dogs: payload, filt: payload };
    case GET_ALL_TEMPERAMENTS:
      return { ...state, temperaments: payload.temperaments };
    case GET_DOGS:
      return { ...state, filt: payload };
    case GET_DETAIL:
      const detail = state.filt.filter((dog) => dog.id === payload);
      return { filt: detail };
    case FILT_BDD:
      return { ...state, filt: payload };
    case FILT_API:
      return { ...state, filt: payload };
    case GET_NAME:
      return { ...state, filt: payload };
    default:
      return { ...state };
  }
};

export default rootReducer;
