export default function validation(form, setErrors, errors, weightH, heightH) {
  if (!form.name) {
    console.log("falta nombre");
    setErrors({ ...errors, name: "Nombre vacio" });
    return errors;
  }
  const newErrors = {};

  if (!/([a-zA-Z_\s]+)/.test(form.name)) {
    newErrors.name = "Nombre invalido";
  }

  setErrors(newErrors);
  return newErrors;
}
